// Algae Farm 1 Data Simulation with strict +/-2% corridor (consecutive values)
const now = new Date();

const INITIAL_VALUES = {
    "temperature": 25.0,
    "ph": 7.5,
    "nitrogen": 15.0,
    "phosphorus": 2.0,
    "biomass": 500.0,
    "biomass_production": 50.0,
    "co2_consumption": 90.0,
    "oxygen_production": 60.0,
    "growth_rate": 10.0
};

const LIMITS = {
    "temperature": {
        "min": 15,
        "max": 40
    },
    "ph": {
        "min": 6.5,
        "max": 8.5
    },
    "nitrogen": {
        "min": 5,
        "max": 30
    },
    "phosphorus": {
        "min": 0.5,
        "max": 5
    },
    "biomass": {
        "min": 100,
        "max": 1000
    },
    "biomass_production": {
        "min": 0,
        "max": 100
    },
    "co2_consumption": {
        "min": 0,
        "max": 200
    },
    "oxygen_production": {
        "min": 0,
        "max": 150
    },
    "growth_rate": {
        "min": 0,
        "max": 20
    }
};

// Initialize state to track previous values
if (!global.state_algy) {
    global.state_algy = INITIAL_VALUES;
}

const prev = global.state_algy;

// Generate next value within +/-2% of previous value
function nextValue(key) {
    const prevVal = prev[key];
    if (typeof prevVal !== 'number') {
        return prevVal;
    }

    // Calculate +/-2% corridor
    const corridor = Math.abs(prevVal) * 0.02;
    const change = (Math.random() - 0.5) * 2 * corridor;
    const newVal = prevVal + change;

    // Apply physical limits
    const limit = LIMITS[key] || {};
    const min = typeof limit.min === 'number' ? limit.min : newVal;
    const max = typeof limit.max === 'number' ? limit.max : newVal;

    return Math.max(min, Math.min(max, newVal));
}

function roundValue(value) {
    return Math.round(value * 100) / 100;
}

// Generate data
const data = {};
for (const key of Object.keys(INITIAL_VALUES)) {
    data[key] = roundValue(nextValue(key));
}

// Update state with new values
global.state_algy = data;

const payload = {
    device_id: 'algy',
    device_type: 'algae_farm',
    timestamp: now.toISOString(),
    data,
    status: 'operational',
    location: 'site_f',
    fault_type: null
};

msg.payload = payload;
msg.topic = 'devices/algae_farm/algy/telemetry';

return msg;

// Validate algae farm data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    temperature: { min: 15, max: 40 },
    ph: { min: 6.0, max: 9.0 },
    nitrogen: { min: 0, max: 30 },
    phosphorus: { min: 0, max: 5 },
    biomass: { min: 0, max: 1500 },
    biomass_production: { min: 0, max: 100 },
    co2_consumption: { min: 0, max: 200 },
    oxygen_production: { min: 0, max: 150 },
    growth_rate: { min: 0, max: 20 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.biomass_production > 0 && data.data.co2_consumption < data.data.biomass_production * 1.5) {
    errors.push('CO2 consumption too low for biomass production');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const temperature = parseFloat(data.data.temperature) || 0;
const ph = parseFloat(data.data.ph) || 0;
const nitrogen = parseFloat(data.data.nitrogen) || 0;
const phosphorus = parseFloat(data.data.phosphorus) || 0;
const biomass = parseFloat(data.data.biomass) || 0;
const biomassProduction = parseFloat(data.data.biomass_production) || 0;
const co2Consumption = parseFloat(data.data.co2_consumption) || 0;
const oxygenProduction = parseFloat(data.data.oxygen_production) || 0;
const growthRate = parseFloat(data.data.growth_rate) || 0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    temperature: temperature,
    ph: ph,
    nitrogen: nitrogen,
    phosphorus: phosphorus,
    biomass: biomass,
    biomass_production: biomassProduction,
    co2_consumption: co2Consumption,
    oxygen_production: oxygenProduction,
    growth_rate: growthRate
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ Algae Farm 1 data transformed for InfluxDB:');
node.log('  - Temperature: ' + temperature + ' °C');
node.log('  - pH: ' + ph);
node.log('  - Nitrogen: ' + nitrogen + ' mg/L');
node.log('  - Phosphorus: ' + phosphorus + ' mg/L');
node.log('  - Biomass: ' + biomass + ' kg/m³');
node.log('  - Biomass Production: ' + biomassProduction + ' kg/m³/day');
node.log('  - CO2 Consumption: ' + co2Consumption + ' g/m³/h');
node.log('  - Oxygen Production: ' + oxygenProduction + ' g/m³/h');
node.log('  - Growth Rate: ' + growthRate + ' %/day');
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
