// Algae Farm 2 Data Simulation
const now = new Date();
const hour = now.getHours();
const dayOfYear = Math.floor((now - new Date(now.getFullYear(), 0, 0)) / (1000 * 60 * 60 * 24));

// Initialize global state for algae farm
if (!global.algaeFarm2State) {
    global.algaeFarm2State = {
        biomass: 600, // kg/m³
        lastUpdate: now.getTime()
    };
}

// Temperature model (°C)
function calculateTemperature() {
    const baseTemp = 25;
    const seasonalFactor = 1 + 0.2 * Math.sin(2 * Math.PI * (dayOfYear - 172) / 365);
    const dailyFactor = 1 + 0.1 * Math.sin(2 * Math.PI * (hour - 12) / 24);
    const noise = (Math.random() - 0.5) * 2;
    return baseTemp * seasonalFactor * dailyFactor + noise;
}

// pH level model
function calculatePH() {
    const basePH = 7.5;
    const variation = (Math.random() - 0.5) * 0.5;
    return Math.max(6.5, Math.min(8.5, basePH + variation));
}

// Nutrient levels (mg/L)
function calculateNutrients() {
    const baseNitrogen = 18;
    const basePhosphorus = 2.5;
    const consumptionRate = 0.12;
    const noise = (Math.random() - 0.5) * 1;
    return {
        nitrogen: Math.max(5, baseNitrogen - consumptionRate + noise),
        phosphorus: Math.max(0.5, basePhosphorus - consumptionRate * 0.1 + noise * 0.1)
    };
}

// Biomass production model (kg/m³/day)
function calculateBiomassProduction(temperature, ph, nutrients) {
    const optimalTemp = 25;
    const optimalPH = 7.5;
    const tempFactor = 1 - 0.02 * Math.abs(temperature - optimalTemp);
    const phFactor = 1 - 0.1 * Math.abs(ph - optimalPH);
    const nutrientFactor = Math.min(1, (nutrients.nitrogen / 18) * (nutrients.phosphorus / 2.5));
    const baseProduction = 60; // Slightly higher for farm 2
    const noise = (Math.random() - 0.5) * 5;
    return Math.max(0, baseProduction * tempFactor * phFactor * nutrientFactor + noise);
}

// CO2 consumption model (g/m³/h)
function calculateCO2Consumption(biomassProduction) {
    const co2PerBiomass = 1.8; // g CO2 per g biomass
    const noise = (Math.random() - 0.5) * 2;
    return Math.max(0, biomassProduction * co2PerBiomass + noise);
}

// Oxygen production model (g/m³/h)
function calculateOxygenProduction(biomassProduction) {
    const o2PerBiomass = 1.2; // g O2 per g biomass
    const noise = (Math.random() - 0.5) * 1.5;
    return Math.max(0, biomassProduction * o2PerBiomass + noise);
}

// Growth rate model (%/day)
function calculateGrowthRate(biomassProduction, currentBiomass) {
    if (currentBiomass <= 0) return 0;
    return (biomassProduction / currentBiomass) * 100;
}

// Generate data with fault scenarios
function generateAlgaeFarm2Data() {
    const faultProbability = 0.001;
    const isFault = Math.random() < faultProbability;

    let temperature = calculateTemperature();
    let ph = calculatePH();
    let nutrients = calculateNutrients();
    let biomassProduction = calculateBiomassProduction(temperature, ph, nutrients);
    let co2Consumption = calculateCO2Consumption(biomassProduction);
    let oxygenProduction = calculateOxygenProduction(biomassProduction);

    // Update biomass state
    const timeDiff = (now.getTime() - global.algaeFarm2State.lastUpdate) / (1000 * 60 * 60 * 24);
    global.algaeFarm2State.biomass = Math.max(100, Math.min(1200, global.algaeFarm2State.biomass + biomassProduction * timeDiff));
    global.algaeFarm2State.lastUpdate = now.getTime();

    let growthRate = calculateGrowthRate(biomassProduction, global.algaeFarm2State.biomass);

    // Fault scenarios
    if (isFault) {
        const faultType = Math.floor(Math.random() * 4);
        switch (faultType) {
            case 0: // Temperature fault
                temperature += 10;
                biomassProduction *= 0.5;
                growthRate *= 0.5;
                break;
            case 1: // pH imbalance
                ph = 6.0;
                biomassProduction *= 0.4;
                growthRate *= 0.4;
                break;
            case 2: // Nutrient deficiency
                nutrients.nitrogen *= 0.3;
                nutrients.phosphorus *= 0.3;
                biomassProduction *= 0.6;
                growthRate *= 0.6;
                break;
            case 3: // Contamination
                biomassProduction *= 0.3;
                growthRate *= 0.3;
                oxygenProduction *= 0.5;
                break;
        }
    }

    return {
        device_id: 'big_algy',
        device_type: 'algae_farm',
        timestamp: now.toISOString(),
        data: {
            temperature: Math.round(temperature * 100) / 100,
            ph: Math.round(ph * 100) / 100,
            nitrogen: Math.round(nutrients.nitrogen * 100) / 100,
            phosphorus: Math.round(nutrients.phosphorus * 100) / 100,
            biomass: Math.round(global.algaeFarm2State.biomass * 100) / 100,
            biomass_production: Math.round(biomassProduction * 100) / 100,
            co2_consumption: Math.round(co2Consumption * 100) / 100,
            oxygen_production: Math.round(oxygenProduction * 100) / 100,
            growth_rate: Math.round(growthRate * 100) / 100
        },
        status: isFault ? 'fault' : 'operational',
        location: 'site_g',
        fault_type: isFault ? ['temperature', 'ph_imbalance', 'nutrient_deficiency', 'contamination'][faultType] : null
    };
}

// Generate and return data
const bigAlgyData = generateAlgaeFarm2Data();
msg.payload = bigAlgyData;
msg.topic = `devices/algae_farm/${bigAlgyData.device_id}/telemetry`;

return msg;

// Validate algae farm data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    temperature: { min: 15, max: 40 },
    ph: { min: 6.0, max: 9.0 },
    nitrogen: { min: 0, max: 30 },
    phosphorus: { min: 0, max: 5 },
    biomass: { min: 0, max: 1500 },
    biomass_production: { min: 0, max: 100 },
    co2_consumption: { min: 0, max: 200 },
    oxygen_production: { min: 0, max: 150 },
    growth_rate: { min: 0, max: 20 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.biomass_production > 0 && data.data.co2_consumption < data.data.biomass_production * 1.5) {
    errors.push('CO2 consumption too low for biomass production');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const temperature = parseFloat(data.data.temperature) || 0;
const ph = parseFloat(data.data.ph) || 0;
const nitrogen = parseFloat(data.data.nitrogen) || 0;
const phosphorus = parseFloat(data.data.phosphorus) || 0;
const biomass = parseFloat(data.data.biomass) || 0;
const biomassProduction = parseFloat(data.data.biomass_production) || 0;
const co2Consumption = parseFloat(data.data.co2_consumption) || 0;
const oxygenProduction = parseFloat(data.data.oxygen_production) || 0;
const growthRate = parseFloat(data.data.growth_rate) || 0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    temperature: temperature,
    ph: ph,
    nitrogen: nitrogen,
    phosphorus: phosphorus,
    biomass: biomass,
    biomass_production: biomassProduction,
    co2_consumption: co2Consumption,
    oxygen_production: oxygenProduction,
    growth_rate: growthRate
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ Algae Farm 2 data transformed for InfluxDB:');
node.log('  - Temperature: ' + temperature + ' °C');
node.log('  - pH: ' + ph);
node.log('  - Nitrogen: ' + nitrogen + ' mg/L');
node.log('  - Phosphorus: ' + phosphorus + ' mg/L');
node.log('  - Biomass: ' + biomass + ' kg/m³');
node.log('  - Biomass Production: ' + biomassProduction + ' kg/m³/day');
node.log('  - CO2 Consumption: ' + co2Consumption + ' g/m³/h');
node.log('  - Oxygen Production: ' + oxygenProduction + ' g/m³/h');
node.log('  - Growth Rate: ' + growthRate + ' %/day');
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
