// Biogas Plant Data Simulation
const now = new Date();
const hour = now.getHours();
const dayOfYear = Math.floor((now - new Date(now.getFullYear(), 0, 0)) / (1000 * 60 * 60 * 24));

// Initialize global state for parameter tracking
if (!global.biogasState) {
    global.biogasState = {
        temperature: 35,
        ph: 7.2,
        gasFlowRate: 50,
        methaneConcentration: 65,
        pressure: 1.2,
        energyContent: 6.5
    };
}

// Helper function to limit changes to 3%
function limitChange(newValue, previousValue) {
    if (previousValue === 0 || previousValue === undefined) return newValue;
    const maxChange = previousValue * 0.03;
    const change = newValue - previousValue;
    if (Math.abs(change) > maxChange) {
        return previousValue + (change > 0 ? maxChange : -maxChange);
    }
    return newValue;
}

// Temperature model (°C)
function calculateTemperature() {
    const baseTemp = 35;
    const seasonalFactor = 1 + 0.1 * Math.sin(2 * Math.PI * (dayOfYear - 172) / 365);
    const dailyFactor = 1 + 0.05 * Math.sin(2 * Math.PI * (hour - 12) / 24);
    const noise = (Math.random() - 0.5) * baseTemp * 0.03; // Limited to 3%
    const newValue = baseTemp * seasonalFactor * dailyFactor + noise;
    return limitChange(newValue, global.biogasState.temperature);
}

// pH level model
function calculatePH() {
    const basePH = 7.2;
    const variation = (Math.random() - 0.5) * basePH * 0.03; // Limited to 3%
    const newValue = Math.max(6.5, Math.min(8.0, basePH + variation));
    return limitChange(newValue, global.biogasState.ph);
}

// Gas flow rate model (m³/h)
function calculateGasFlowRate(temperature, ph) {
    const baseFlowRate = 50;
    const tempFactor = 1 + 0.02 * (temperature - 35);
    const phFactor = 1 - 0.1 * Math.abs(ph - 7.2);
    const noise = (Math.random() - 0.5) * baseFlowRate * 0.03; // Limited to 3%
    const newValue = Math.max(0, baseFlowRate * tempFactor * phFactor + noise);
    return limitChange(newValue, global.biogasState.gasFlowRate);
}

// Methane concentration model (%)
function calculateMethaneConcentration(temperature, ph) {
    const baseMethane = 65;
    const tempFactor = 1 + 0.01 * (temperature - 35);
    const phFactor = 1 - 0.05 * Math.abs(ph - 7.2);
    const noise = (Math.random() - 0.5) * baseMethane * 0.03; // Limited to 3%
    const newValue = Math.max(50, Math.min(80, baseMethane * tempFactor * phFactor + noise));
    return limitChange(newValue, global.biogasState.methaneConcentration);
}

// Pressure model (bar)
function calculatePressure(gasFlowRate) {
    const basePressure = 1.2;
    const flowFactor = 1 + 0.01 * (gasFlowRate - 50);
    const noise = (Math.random() - 0.5) * basePressure * 0.03; // Limited to 3%
    const newValue = Math.max(0.8, basePressure * flowFactor + noise);
    return limitChange(newValue, global.biogasState.pressure);
}

// Energy content model (kWh/m³)
function calculateEnergyContent(methaneConcentration) {
    const baseEnergy = 6.5;
    const methaneFactor = methaneConcentration / 65;
    const noise = (Math.random() - 0.5) * baseEnergy * 0.03; // Limited to 3%
    const newValue = baseEnergy * methaneFactor + noise;
    return limitChange(newValue, global.biogasState.energyContent);
}

// Generate data with fault scenarios
function generateBiogasData() {
    const faultProbability = 0.001;
    const isFault = Math.random() < faultProbability;

    let temperature = calculateTemperature();
    let ph = calculatePH();
    let gasFlowRate = calculateGasFlowRate(temperature, ph);
    let methaneConcentration = calculateMethaneConcentration(temperature, ph);
    let pressure = calculatePressure(gasFlowRate);
    let energyContent = calculateEnergyContent(methaneConcentration);

    // Fault scenarios (faults can exceed 3% limit)
    if (isFault) {
        const faultType = Math.floor(Math.random() * 4);
        switch (faultType) {
            case 0: // Temperature fault
                temperature += 15;
                gasFlowRate *= 0.7;
                methaneConcentration *= 0.8;
                break;
            case 1: // pH imbalance
                ph = 6.0;
                gasFlowRate *= 0.5;
                methaneConcentration *= 0.6;
                break;
            case 2: // Gas leak
                pressure *= 0.6;
                gasFlowRate *= 0.8;
                break;
            case 3: // Digester fault
                gasFlowRate *= 0.3;
                methaneConcentration *= 0.7;
                pressure *= 0.8;
                break;
        }
    }

    // Update state with final values
    global.biogasState.temperature = temperature;
    global.biogasState.ph = ph;
    global.biogasState.gasFlowRate = gasFlowRate;
    global.biogasState.methaneConcentration = methaneConcentration;
    global.biogasState.pressure = pressure;
    global.biogasState.energyContent = energyContent;

    return {
        device_id: 'biogas',
        device_type: 'biogas_plant',
        timestamp: now.toISOString(),
        data: {
            temperature: Math.round(temperature * 100) / 100,
            ph: Math.round(ph * 100) / 100,
            gas_flow_rate: Math.round(gasFlowRate * 100) / 100,
            methane_concentration: Math.round(methaneConcentration * 100) / 100,
            pressure: Math.round(pressure * 100) / 100,
            energy_content: Math.round(energyContent * 100) / 100
        },
        status: isFault ? 'fault' : 'operational',
        location: 'site_c',
        fault_type: isFault ? ['temperature', 'ph_imbalance', 'gas_leak', 'digester'][faultType] : null
    };
}

// Generate and return data
const bgData = generateBiogasData();
msg.payload = bgData;
msg.topic = `devices/biogas_plant/${bgData.device_id}/telemetry`;

return msg;

// Validate biogas plant data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    temperature: { min: 20, max: 60 },
    ph: { min: 6.0, max: 8.5 },
    gas_flow_rate: { min: 0, max: 100 },
    methane_concentration: { min: 40, max: 85 },
    pressure: { min: 0.5, max: 2.0 },
    energy_content: { min: 4.0, max: 8.0 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.methane_concentration < 50 && data.data.gas_flow_rate > 20) {
    errors.push('High gas flow with low methane concentration');
}

if (data.data.pressure < 0.8 && data.data.gas_flow_rate > 30) {
    errors.push('High gas flow with low pressure');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const temperature = parseFloat(data.data.temperature) || 0;
const ph = parseFloat(data.data.ph) || 0;
const gasFlowRate = parseFloat(data.data.gas_flow_rate) || 0;
const methaneConcentration = parseFloat(data.data.methane_concentration) || 0;
const pressure = parseFloat(data.data.pressure) || 0;
const energyContent = parseFloat(data.data.energy_content) || 0;

// Calculate efficiency if not provided
const efficiency = data.data.efficiency ? parseFloat(data.data.efficiency) : 
    (gasFlowRate > 0 && methaneConcentration > 50) ? (methaneConcentration / 65) * 100 : 0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    temperature: temperature,
    ph: ph,
    gas_flow_rate: gasFlowRate,
    methane_concentration: methaneConcentration,
    pressure: pressure,
    energy_content: energyContent,
    efficiency: efficiency
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ Biogas plant data transformed for InfluxDB:');
node.log('  - Temperature: ' + temperature + ' °C');
node.log('  - pH: ' + ph);
node.log('  - Gas Flow Rate: ' + gasFlowRate + ' m³/h');
node.log('  - Methane Concentration: ' + methaneConcentration + ' %');
node.log('  - Pressure: ' + pressure + ' bar');
node.log('  - Energy Content: ' + energyContent + ' kWh/m³');
node.log('  - Efficiency: ' + efficiency + ' %');
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
