// Energy Storage Data Simulation with strict +/-2% corridor (consecutive values)
const now = new Date();

const INITIAL_VALUES = {
    "temperature": 25.0,
    "state_of_charge": 50.0,
    "voltage": 48.0,
    "current": 10.0,
    "power": 480.0,
    "capacity": 100.0,
    "degradation": 1.0,
    "efficiency": 92.0
};

const LIMITS = {
    "temperature": {
        "min": -10,
        "max": 60
    },
    "state_of_charge": {
        "min": 5,
        "max": 100
    },
    "voltage": {
        "min": 40,
        "max": 60
    },
    "current": {
        "min": -50,
        "max": 50
    },
    "power": {
        "min": -2000,
        "max": 2000
    },
    "capacity": {
        "min": 50,
        "max": 120
    },
    "degradation": {
        "min": 0.7,
        "max": 1
    },
    "efficiency": {
        "min": 85,
        "max": 98
    }
};

// Initialize state to track previous values
if (!global.state_turbine_is) {
    global.state_turbine_is = INITIAL_VALUES;
}

const prev = global.state_turbine_is;

// Generate next value within +/-2% of previous value
function nextValue(key) {
    const prevVal = prev[key];
    if (typeof prevVal !== 'number') {
        return prevVal;
    }

    // Calculate +/-2% corridor
    const corridor = Math.abs(prevVal) * 0.02;
    const change = (Math.random() - 0.5) * 2 * corridor;
    const newVal = prevVal + change;

    // Apply physical limits
    const limit = LIMITS[key] || {};
    const min = typeof limit.min === 'number' ? limit.min : newVal;
    const max = typeof limit.max === 'number' ? limit.max : newVal;

    return Math.max(min, Math.min(max, newVal));
}

function roundValue(value) {
    return Math.round(value * 100) / 100;
}

// Generate data
const data = {};
for (const key of Object.keys(INITIAL_VALUES)) {
    data[key] = roundValue(nextValue(key));
}

// Update state with new values
global.state_turbine_is = data;

const payload = {
    device_id: 'turbine_is',
    device_type: 'energy_storage',
    timestamp: now.toISOString(),
    data,
    status: 'operational',
    location: 'site_e',
    fault_type: null
};

msg.payload = payload;
msg.topic = 'devices/energy_storage/turbine_is/telemetry';

return msg;

// Validate energy storage data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    temperature: { min: -10, max: 60 },
    state_of_charge: { min: 5, max: 100 },
    voltage: { min: 40, max: 60 },
    current: { min: -50, max: 50 },
    power: { min: -2000, max: 2000 },
    capacity: { min: 50, max: 120 },
    degradation: { min: 0.7, max: 1.0 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.state_of_charge < 10 && data.data.power > 0) {
    errors.push('Charging when SOC is very low');
}

if (data.data.state_of_charge > 95 && data.data.power < 0) {
    errors.push('Discharging when SOC is very high');
}

if (Math.abs(data.data.power - (data.data.voltage * data.data.current)) > 100) {
    errors.push('Power calculation mismatch');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const temperature = parseFloat(data.data.temperature) || 0;
const stateOfCharge = parseFloat(data.data.state_of_charge) || 0;
const voltage = parseFloat(data.data.voltage) || 0;
const current = parseFloat(data.data.current) || 0;
const power = parseFloat(data.data.power) || 0;
const capacity = parseFloat(data.data.capacity) || 0;
const degradation = parseFloat(data.data.degradation) || 1.0;

// Use simulated efficiency (already in ±2% corridor)
const efficiency = parseFloat(data.data.efficiency) || 92.0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    temperature: temperature,
    state_of_charge: stateOfCharge,
    voltage: voltage,
    current: current,
    power: power,
    capacity: capacity,
    degradation: degradation,
    efficiency: efficiency
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ Energy storage data transformed for InfluxDB:');
node.log('  - Temperature: ' + temperature + ' °C');
node.log('  - State of Charge: ' + stateOfCharge + ' %');
node.log('  - Voltage: ' + voltage + ' V');
node.log('  - Current: ' + current + ' A');
node.log('  - Power: ' + power + ' W');
node.log('  - Capacity: ' + capacity + ' kWh');
node.log('  - Degradation: ' + degradation);
node.log('  - Efficiency: ' + efficiency + ' %');
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
