// Engine Test Bench Data Simulation
const now = new Date();
const hour = now.getHours();
const minute = now.getMinutes();
const dayOfYear = Math.floor((now - new Date(now.getFullYear(), 0, 0)) / (1000 * 60 * 60 * 24));

// Test cycle simulation (varies load over time)
function calculateTestCycle() {
    const cycleTime = (hour * 60 + minute) % 60; // 60-minute cycle
    const cycleProgress = cycleTime / 60;
    // Simulate different test phases: idle, ramp up, full load, ramp down
    if (cycleProgress < 0.1) return 0.1; // Idle
    if (cycleProgress < 0.3) return 0.1 + (cycleProgress - 0.1) * 4.5; // Ramp up
    if (cycleProgress < 0.7) return 1.0; // Full load
    if (cycleProgress < 0.9) return 1.0 - (cycleProgress - 0.7) * 4.5; // Ramp down
    return 0.1; // Idle
}

// Engine speed model (RPM)
function calculateEngineSpeed(load) {
    const baseSpeed = 1500;
    const loadFactor = 1 + 0.3 * load;
    const noise = (Math.random() - 0.5) * 50;
    return Math.max(800, Math.min(3000, baseSpeed * loadFactor + noise));
}

// Torque model (Nm)
function calculateTorque(engineSpeed, load) {
    const baseTorque = 200;
    const speedFactor = 1 - 0.2 * (engineSpeed - 1500) / 1500;
    const loadFactor = load;
    const noise = (Math.random() - 0.5) * 10;
    return Math.max(50, baseTorque * speedFactor * loadFactor + noise);
}

// Power output model (kW)
function calculatePowerOutput(engineSpeed, torque) {
    // Power (kW) = (Torque (Nm) * Speed (RPM)) / 9549
    const power = (torque * engineSpeed) / 9549;
    const noise = (Math.random() - 0.5) * 2;
    return Math.max(0, power + noise);
}

// Coolant temperature model (°C)
function calculateCoolantTemperature(engineSpeed, powerOutput) {
    const baseTemp = 85;
    const speedFactor = 1 + 0.1 * (engineSpeed - 1500) / 1500;
    const powerFactor = 1 + 0.15 * (powerOutput - 50) / 50;
    const noise = (Math.random() - 0.5) * 3;
    return Math.max(70, Math.min(100, baseTemp * speedFactor * powerFactor + noise));
}

// Oil temperature model (°C)
function calculateOilTemperature(coolantTemp, engineSpeed) {
    const baseTemp = coolantTemp + 5;
    const speedFactor = 1 + 0.05 * (engineSpeed - 1500) / 1500;
    const noise = (Math.random() - 0.5) * 2;
    return Math.max(75, Math.min(110, baseTemp * speedFactor + noise));
}

// Exhaust temperature model (°C)
function calculateExhaustTemperature(powerOutput, engineSpeed) {
    const baseTemp = 400;
    const powerFactor = 1 + 0.3 * (powerOutput - 50) / 50;
    const speedFactor = 1 + 0.1 * (engineSpeed - 1500) / 1500;
    const noise = (Math.random() - 0.5) * 20;
    return Math.max(300, Math.min(600, baseTemp * powerFactor * speedFactor + noise));
}

// Fuel consumption model (L/h)
function calculateFuelConsumption(powerOutput, engineSpeed) {
    const baseConsumption = 15;
    const powerFactor = 1 + 0.8 * (powerOutput - 50) / 50;
    const speedFactor = 1 + 0.2 * (engineSpeed - 1500) / 1500;
    const noise = (Math.random() - 0.5) * 1.5;
    return Math.max(5, baseConsumption * powerFactor * speedFactor + noise);
}

// Oil pressure model (bar)
function calculateOilPressure(engineSpeed, oilTemp) {
    const basePressure = 4.0;
    const speedFactor = 1 + 0.3 * (engineSpeed - 1500) / 1500;
    const tempFactor = 1 - 0.1 * (oilTemp - 90) / 90;
    const noise = (Math.random() - 0.5) * 0.3;
    return Math.max(2.0, Math.min(6.0, basePressure * speedFactor * tempFactor + noise));
}

// Fuel pressure model (bar)
function calculateFuelPressure(engineSpeed) {
    const basePressure = 3.5;
    const speedFactor = 1 + 0.1 * (engineSpeed - 1500) / 1500;
    const noise = (Math.random() - 0.5) * 0.2;
    return Math.max(2.5, Math.min(4.5, basePressure * speedFactor + noise));
}

// Efficiency model (%)
function calculateEfficiency(powerOutput, fuelConsumption) {
    const fuelEnergyDensity = 9.7; // kWh/L for diesel
    const energyInput = fuelConsumption * fuelEnergyDensity;
    const efficiency = (powerOutput / energyInput) * 100;
    const noise = (Math.random() - 0.5) * 1;
    return Math.max(25, Math.min(45, efficiency + noise));
}

// Load model (%)
function calculateLoad(loadFactor) {
    return Math.round(loadFactor * 100);
}

// Generate data with fault scenarios
function generateEngineTestBenchData() {
    const faultProbability = 0.001;
    const isFault = Math.random() < faultProbability;

    const loadFactor = calculateTestCycle();
    let engineSpeed = calculateEngineSpeed(loadFactor);
    let torque = calculateTorque(engineSpeed, loadFactor);
    let powerOutput = calculatePowerOutput(engineSpeed, torque);
    let coolantTemp = calculateCoolantTemperature(engineSpeed, powerOutput);
    let oilTemp = calculateOilTemperature(coolantTemp, engineSpeed);
    let exhaustTemp = calculateExhaustTemperature(powerOutput, engineSpeed);
    let fuelConsumption = calculateFuelConsumption(powerOutput, engineSpeed);
    let oilPressure = calculateOilPressure(engineSpeed, oilTemp);
    let fuelPressure = calculateFuelPressure(engineSpeed);
    let efficiency = calculateEfficiency(powerOutput, fuelConsumption);
    let load = calculateLoad(loadFactor);

    // Fault scenarios
    if (isFault) {
        const faultType = Math.floor(Math.random() * 5);
        switch (faultType) {
            case 0: // Low oil pressure fault
                oilPressure *= 0.6;
                oilTemp += 10;
                efficiency *= 0.9;
                break;
            case 1: // Overheating fault
                coolantTemp += 15;
                oilTemp += 15;
                exhaustTemp += 50;
                efficiency *= 0.85;
                break;
            case 2: // Fuel supply fault
                fuelPressure *= 0.7;
                fuelConsumption *= 0.8;
                powerOutput *= 0.8;
                break;
            case 3: // Low efficiency fault
                efficiency *= 0.7;
                fuelConsumption *= 1.2;
                exhaustTemp += 30;
                break;
            case 4: // High vibration (simulated by speed fluctuation)
                engineSpeed += (Math.random() - 0.5) * 200;
                torque *= 0.9;
                powerOutput *= 0.9;
                break;
        }
    }

    return {
        device_id: 'engine_bench',
        device_type: 'engine_test_bench',
        timestamp: now.toISOString(),
        data: {
            engine_speed: Math.round(engineSpeed),
            torque: Math.round(torque * 100) / 100,
            power_output: Math.round(powerOutput * 100) / 100,
            coolant_temperature: Math.round(coolantTemp * 100) / 100,
            oil_temperature: Math.round(oilTemp * 100) / 100,
            exhaust_temperature: Math.round(exhaustTemp * 100) / 100,
            fuel_consumption: Math.round(fuelConsumption * 100) / 100,
            oil_pressure: Math.round(oilPressure * 100) / 100,
            fuel_pressure: Math.round(fuelPressure * 100) / 100,
            efficiency: Math.round(efficiency * 100) / 100,
            load: load
        },
        status: isFault ? 'fault' : 'operational',
        location: 'site_e',
        fault_type: isFault ? ['low_oil_pressure', 'overheating', 'fuel_supply', 'low_efficiency', 'vibration'][faultType] : null
    };
}

// Generate and return data
const etbData = generateEngineTestBenchData();
msg.payload = etbData;
msg.topic = `devices/engine_test_bench/${etbData.device_id}/telemetry`;

return msg;

// Validate engine test bench data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    engine_speed: { min: 500, max: 3500 },
    torque: { min: 0, max: 500 },
    power_output: { min: 0, max: 200 },
    coolant_temperature: { min: 60, max: 110 },
    oil_temperature: { min: 70, max: 120 },
    exhaust_temperature: { min: 200, max: 700 },
    fuel_consumption: { min: 0, max: 50 },
    oil_pressure: { min: 1.5, max: 7.0 },
    fuel_pressure: { min: 2.0, max: 5.0 },
    efficiency: { min: 20, max: 50 },
    load: { min: 0, max: 100 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.oil_temperature < data.data.coolant_temperature) {
    errors.push('Oil temperature below coolant temperature');
}

if (data.data.exhaust_temperature < data.data.coolant_temperature) {
    errors.push('Exhaust temperature below coolant temperature');
}

if (data.data.efficiency > 50) {
    errors.push('Efficiency exceeds realistic maximum for diesel engine');
}

if (data.data.oil_pressure < 2.0 && data.data.engine_speed > 2000) {
    errors.push('Low oil pressure at high engine speed');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const engineSpeed = parseFloat(data.data.engine_speed) || 0;
const torque = parseFloat(data.data.torque) || 0;
const powerOutput = parseFloat(data.data.power_output) || 0;
const coolantTemp = parseFloat(data.data.coolant_temperature) || 0;
const oilTemp = parseFloat(data.data.oil_temperature) || 0;
const exhaustTemp = parseFloat(data.data.exhaust_temperature) || 0;
const fuelConsumption = parseFloat(data.data.fuel_consumption) || 0;
const oilPressure = parseFloat(data.data.oil_pressure) || 0;
const fuelPressure = parseFloat(data.data.fuel_pressure) || 0;
const efficiency = parseFloat(data.data.efficiency) || 0;
const load = parseFloat(data.data.load) || 0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    engine_speed: engineSpeed,
    torque: torque,
    power_output: powerOutput,
    coolant_temperature: coolantTemp,
    oil_temperature: oilTemp,
    exhaust_temperature: exhaustTemp,
    fuel_consumption: fuelConsumption,
    oil_pressure: oilPressure,
    fuel_pressure: fuelPressure,
    efficiency: efficiency,
    load: load
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ Engine test bench data transformed for InfluxDB:');
node.log('  - Engine Speed: ' + engineSpeed + ' RPM');
node.log('  - Torque: ' + torque + ' Nm');
node.log('  - Power Output: ' + powerOutput + ' kW');
node.log('  - Coolant Temperature: ' + coolantTemp + ' °C');
node.log('  - Oil Temperature: ' + oilTemp + ' °C');
node.log('  - Exhaust Temperature: ' + exhaustTemp + ' °C');
node.log('  - Fuel Consumption: ' + fuelConsumption + ' L/h');
node.log('  - Oil Pressure: ' + oilPressure + ' bar');
node.log('  - Fuel Pressure: ' + fuelPressure + ' bar');
node.log('  - Efficiency: ' + efficiency + ' %');
node.log('  - Load: ' + load + ' %');
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
