// Engine Test Bench Data Simulation with strict +/-2% corridor
const now = new Date();

const BASE_VALUES = {
    "engine_speed": 1500,
    "torque": 150,
    "power_output": 23.57,
    "coolant_temperature": 85,
    "oil_temperature": 90,
    "exhaust_temperature": 400,
    "fuel_consumption": 15,
    "oil_pressure": 4,
    "fuel_pressure": 3.5,
    "efficiency": 35,
    "load": 50
};

const LIMITS = {
    "engine_speed": {
        "min": 800,
        "max": 3000
    },
    "torque": {
        "min": 50,
        "max": 300
    },
    "power_output": {
        "min": 0,
        "max": 100
    },
    "coolant_temperature": {
        "min": 70,
        "max": 100
    },
    "oil_temperature": {
        "min": 75,
        "max": 110
    },
    "exhaust_temperature": {
        "min": 300,
        "max": 600
    },
    "fuel_consumption": {
        "min": 5,
        "max": 30
    },
    "oil_pressure": {
        "min": 2,
        "max": 6
    },
    "fuel_pressure": {
        "min": 2.5,
        "max": 4.5
    },
    "efficiency": {
        "min": 25,
        "max": 45
    },
    "load": {
        "min": 0,
        "max": 100
    }
};

function randomWithinCorridor(key) {
    const base = BASE_VALUES[key];
    if (typeof base !== 'number') {
        return base;
    }
    const corridor = Math.abs(base) * 0.02;
    const limit = LIMITS[key] || {};
    const min = typeof limit.min === 'number' ? limit.min : base - corridor;
    const max = typeof limit.max === 'number' ? limit.max : base + corridor;
    const noise = corridor === 0 ? 0 : (Math.random() - 0.5) * 2 * corridor;
    const value = base + noise;
    return Math.max(min, Math.min(max, value));
}

function roundValue(value) {
    return Math.round(value * 100) / 100;
}

const data = {};
for (const key of Object.keys(BASE_VALUES)) {
    data[key] = roundValue(randomWithinCorridor(key));
}

const payload = {
    device_id: 'engine_bench',
    device_type: 'engine_test_bench',
    timestamp: now.toISOString(),
    data,
    status: 'operational',
    location: 'site_e',
    fault_type: null
};

msg.payload = payload;
msg.topic = 'devices/engine_test_bench/engine_bench/telemetry';

return msg;

// Validate engine test bench data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    engine_speed: { min: 500, max: 3500 },
    torque: { min: 0, max: 500 },
    power_output: { min: 0, max: 200 },
    coolant_temperature: { min: 60, max: 110 },
    oil_temperature: { min: 70, max: 120 },
    exhaust_temperature: { min: 200, max: 700 },
    fuel_consumption: { min: 0, max: 50 },
    oil_pressure: { min: 1.5, max: 7.0 },
    fuel_pressure: { min: 2.0, max: 5.0 },
    efficiency: { min: 20, max: 50 },
    load: { min: 0, max: 100 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.oil_temperature < data.data.coolant_temperature) {
    errors.push('Oil temperature below coolant temperature');
}

if (data.data.exhaust_temperature < data.data.coolant_temperature) {
    errors.push('Exhaust temperature below coolant temperature');
}

if (data.data.efficiency > 50) {
    errors.push('Efficiency exceeds realistic maximum for diesel engine');
}

if (data.data.oil_pressure < 2.0 && data.data.engine_speed > 2000) {
    errors.push('Low oil pressure at high engine speed');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const engineSpeed = parseFloat(data.data.engine_speed) || 0;
const torque = parseFloat(data.data.torque) || 0;
const powerOutput = parseFloat(data.data.power_output) || 0;
const coolantTemp = parseFloat(data.data.coolant_temperature) || 0;
const oilTemp = parseFloat(data.data.oil_temperature) || 0;
const exhaustTemp = parseFloat(data.data.exhaust_temperature) || 0;
const fuelConsumption = parseFloat(data.data.fuel_consumption) || 0;
const oilPressure = parseFloat(data.data.oil_pressure) || 0;
const fuelPressure = parseFloat(data.data.fuel_pressure) || 0;
const efficiency = parseFloat(data.data.efficiency) || 0;
const load = parseFloat(data.data.load) || 0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    engine_speed: engineSpeed,
    torque: torque,
    power_output: powerOutput,
    coolant_temperature: coolantTemp,
    oil_temperature: oilTemp,
    exhaust_temperature: exhaustTemp,
    fuel_consumption: fuelConsumption,
    oil_pressure: oilPressure,
    fuel_pressure: fuelPressure,
    efficiency: efficiency,
    load: load
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ Engine test bench data transformed for InfluxDB:');
node.log('  - Engine Speed: ' + engineSpeed + ' RPM');
node.log('  - Torque: ' + torque + ' Nm');
node.log('  - Power Output: ' + powerOutput + ' kW');
node.log('  - Coolant Temperature: ' + coolantTemp + ' °C');
node.log('  - Oil Temperature: ' + oilTemp + ' °C');
node.log('  - Exhaust Temperature: ' + exhaustTemp + ' °C');
node.log('  - Fuel Consumption: ' + fuelConsumption + ' L/h');
node.log('  - Oil Pressure: ' + oilPressure + ' bar');
node.log('  - Fuel Pressure: ' + fuelPressure + ' bar');
node.log('  - Efficiency: ' + efficiency + ' %');
node.log('  - Load: ' + load + ' %');
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
