// Heat Boiler Data Simulation
const now = new Date();
const hour = now.getHours();
const dayOfYear = Math.floor((now - new Date(now.getFullYear(), 0, 0)) / (1000 * 60 * 60 * 24));

// Ambient temperature model (°C)
function calculateAmbientTemperature() {
    const baseTemp = 20;
    const seasonalFactor = 1 + 0.4 * Math.sin(2 * Math.PI * (dayOfYear - 172) / 365);
    const dailyFactor = 1 + 0.3 * Math.sin(2 * Math.PI * (hour - 6) / 24);
    const noise = (Math.random() - 0.5) * 3;
    return baseTemp * seasonalFactor * dailyFactor + noise;
}

// Fuel flow rate model (L/h)
function calculateFuelFlowRate(ambientTemp) {
    const baseFlowRate = 15;
    const tempFactor = 1 + 0.05 * (10 - ambientTemp);
    const demandVariation = 1 + 0.2 * Math.sin(2 * Math.PI * (hour - 6) / 24);
    const noise = (Math.random() - 0.5) * 2;
    return Math.max(5, baseFlowRate * tempFactor * demandVariation + noise);
}

// Water temperature model (°C)
function calculateWaterTemperature(fuelFlowRate, ambientTemp) {
    const baseWaterTemp = 80;
    const flowFactor = 1 + 0.02 * (fuelFlowRate - 15);
    const ambientFactor = 1 + 0.01 * (ambientTemp - 20);
    const noise = (Math.random() - 0.5) * 5;
    return Math.max(60, Math.min(95, baseWaterTemp * flowFactor * ambientFactor + noise));
}

// Pressure model (bar)
function calculatePressure(waterTemp) {
    const basePressure = 2.0;
    const tempFactor = 1 + 0.01 * (waterTemp - 80);
    const noise = (Math.random() - 0.5) * 0.2;
    return Math.max(1.5, basePressure * tempFactor + noise);
}

// Efficiency model (%)
function calculateEfficiency(waterTemp, fuelFlowRate) {
    const baseEfficiency = 85;
    const tempFactor = 1 - 0.002 * Math.abs(waterTemp - 80);
    const flowFactor = 1 - 0.01 * Math.abs(fuelFlowRate - 15);
    const agingFactor = 0.98;
    const noise = (Math.random() - 0.5) * 2;
    return Math.max(70, Math.min(95, baseEfficiency * tempFactor * flowFactor * agingFactor + noise));
}

// Heat output model (kW)
function calculateHeatOutput(fuelFlowRate, efficiency) {
    const fuelEnergyDensity = 10.5; // kWh/L
    const heatOutput = fuelFlowRate * fuelEnergyDensity * (efficiency / 100);
    const noise = (Math.random() - 0.5) * 5;
    return Math.max(0, heatOutput + noise);
}

// Generate data with fault scenarios
function generateHeatBoilerData() {
    const faultProbability = 0.001;
    const isFault = Math.random() < faultProbability;

    let ambientTemp = calculateAmbientTemperature();
    let fuelFlowRate = calculateFuelFlowRate(ambientTemp);
    let waterTemp = calculateWaterTemperature(fuelFlowRate, ambientTemp);
    let pressure = calculatePressure(waterTemp);
    let efficiency = calculateEfficiency(waterTemp, fuelFlowRate);
    let heatOutput = calculateHeatOutput(fuelFlowRate, efficiency);

    // Fault scenarios
    if (isFault) {
        const faultType = Math.floor(Math.random() * 4);
        switch (faultType) {
            case 0: // Low efficiency fault
                efficiency *= 0.7;
                heatOutput *= 0.7;
                fuelFlowRate *= 1.2;
                break;
            case 1: // Pressure fault
                pressure *= 1.5;
                waterTemp += 10;
                efficiency *= 0.8;
                break;
            case 2: // Fuel supply fault
                fuelFlowRate *= 0.6;
                heatOutput *= 0.6;
                waterTemp *= 0.9;
                break;
            case 3: // Temperature sensor fault
                waterTemp += 20;
                pressure *= 1.3;
                efficiency *= 0.9;
                break;
        }
    }

    return {
        device_id: `hb_${String(Math.floor(Math.random() * 10) + 1).padStart(3, '0')}`,
        device_type: 'heat_boiler',
        timestamp: now.toISOString(),
        data: {
            ambient_temperature: Math.round(ambientTemp * 100) / 100,
            fuel_flow_rate: Math.round(fuelFlowRate * 100) / 100,
            water_temperature: Math.round(waterTemp * 100) / 100,
            pressure: Math.round(pressure * 100) / 100,
            efficiency: Math.round(efficiency * 100) / 100,
            heat_output: Math.round(heatOutput * 100) / 100
        },
        status: isFault ? 'fault' : 'operational',
        location: 'site_d',
        fault_type: isFault ? ['low_efficiency', 'pressure', 'fuel_supply', 'sensor'][faultType] : null
    };
}

// Generate and return data
const hbData = generateHeatBoilerData();
msg.payload = hbData;
msg.topic = `devices/heat_boiler/${hbData.device_id}/telemetry`;

return msg;

// Validate heat boiler data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    ambient_temperature: { min: -20, max: 50 },
    fuel_flow_rate: { min: 0, max: 30 },
    water_temperature: { min: 50, max: 100 },
    pressure: { min: 1.0, max: 4.0 },
    efficiency: { min: 60, max: 98 },
    heat_output: { min: 0, max: 300 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.water_temperature < data.data.ambient_temperature) {
    errors.push('Water temperature below ambient temperature');
}

if (data.data.efficiency > 95) {
    errors.push('Efficiency exceeds realistic maximum');
}

if (data.data.pressure > 3.5 && data.data.water_temperature < 90) {
    errors.push('High pressure with low water temperature');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const ambientTemp = parseFloat(data.data.ambient_temperature) || 0;
const fuelFlowRate = parseFloat(data.data.fuel_flow_rate) || 0;
const waterTemp = parseFloat(data.data.water_temperature) || 0;
const pressure = parseFloat(data.data.pressure) || 0;
const efficiency = parseFloat(data.data.efficiency) || 0;
const heatOutput = parseFloat(data.data.heat_output) || 0;

// Calculate COP (Coefficient of Performance) if not provided
const cop = data.data.cop ? parseFloat(data.data.cop) : 
    (heatOutput > 0 && fuelFlowRate > 0) ? (heatOutput / (fuelFlowRate * 10.5)) : 0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    ambient_temperature: ambientTemp,
    fuel_flow_rate: fuelFlowRate,
    water_temperature: waterTemp,
    pressure: pressure,
    efficiency: efficiency,
    heat_output: heatOutput,
    cop: cop
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ Heat boiler data transformed for InfluxDB:');
node.log('  - Ambient Temperature: ' + ambientTemp + ' °C');
node.log('  - Fuel Flow Rate: ' + fuelFlowRate + ' L/h');
node.log('  - Water Temperature: ' + waterTemp + ' °C');
node.log('  - Pressure: ' + pressure + ' bar');
node.log('  - Efficiency: ' + efficiency + ' %');
node.log('  - Heat Output: ' + heatOutput + ' kW');
node.log('  - COP: ' + cop);
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
