// Photovoltaic Panels Data Simulation
const now = new Date();
const hour = now.getHours();
const dayOfYear = Math.floor((now - new Date(now.getFullYear(), 0, 0)) / (1000 * 60 * 60 * 24));

// Initialize global state for parameter tracking
if (!global.pvHybridState) {
    global.pvHybridState = {
        irradiance: 1000,
        temperature: 25,
        voltage: 48,
        current: 12,
        powerOutput: 576
    };
}

// Helper function to limit changes to 3%
function limitChange(newValue, previousValue) {
    if (previousValue === 0 || previousValue === undefined) return newValue;
    const maxChange = previousValue * 0.03;
    const change = newValue - previousValue;
    if (Math.abs(change) > maxChange) {
        return previousValue + (change > 0 ? maxChange : -maxChange);
    }
    return newValue;
}

// Solar irradiance model (W/m²)
function calculateIrradiance() {
    const seasonalFactor = 0.5 + 0.5 * Math.cos(2 * Math.PI * (dayOfYear - 172) / 365);
    const solarNoon = 12;
    const dailyFactor = Math.max(0, Math.cos(Math.PI * (hour - solarNoon) / 12));
    const baseIrradiance = 1000;
    const noise = (Math.random() - 0.5) * baseIrradiance * 0.03; // Limited to 3%
    const newValue = Math.max(0, baseIrradiance * seasonalFactor * dailyFactor + noise);
    return limitChange(newValue, global.pvHybridState.irradiance);
}

// Temperature model (°C)
function calculateTemperature() {
    const baseTemp = 25;
    const irradiance = calculateIrradiance();
    const tempRise = irradiance * 0.03;
    const ambientTemp = 20 + 10 * Math.sin(2 * Math.PI * (hour - 6) / 24);
    const noise = (Math.random() - 0.5) * baseTemp * 0.03; // Limited to 3%
    const newValue = baseTemp + tempRise + ambientTemp + noise;
    return limitChange(newValue, global.pvHybridState.temperature);
}

// Voltage model (V)
function calculateVoltage() {
    const baseVoltage = 48;
    const temp = calculateTemperature();
    const tempCoeff = -0.003;
    const voltageVariation = baseVoltage * tempCoeff * (temp - 25) / 100;
    const noise = (Math.random() - 0.5) * baseVoltage * 0.03; // Limited to 3%
    const newValue = Math.max(0, baseVoltage + voltageVariation + noise);
    return limitChange(newValue, global.pvHybridState.voltage);
}

// Current model (A)
function calculateCurrent() {
    const irradiance = calculateIrradiance();
    const baseCurrent = 12;
    const currentVariation = baseCurrent * (irradiance / 1000);
    const noise = (Math.random() - 0.5) * baseCurrent * 0.03; // Limited to 3%
    const newValue = Math.max(0, currentVariation + noise);
    return limitChange(newValue, global.pvHybridState.current);
}

// Power output model (W)
function calculatePowerOutput() {
    const voltage = calculateVoltage();
    const current = calculateCurrent();
    const temp = calculateTemperature();
    const tempEfficiency = 1 - 0.004 * (temp - 25);
    const agingFactor = 0.98;
    const newValue = Math.max(0, voltage * current * tempEfficiency * agingFactor);
    return limitChange(newValue, global.pvHybridState.powerOutput);
}

// Generate data with fault scenarios
function generatePVPanelsData() {
    const faultProbability = 0.001;
    const isFault = Math.random() < faultProbability;

    let irradiance = calculateIrradiance();
    let temperature = calculateTemperature();
    let voltage = calculateVoltage();
    let current = calculateCurrent();
    let powerOutput = calculatePowerOutput();

    // Fault scenarios (faults can exceed 3% limit)
    if (isFault) {
        const faultType = Math.floor(Math.random() * 3);
        switch (faultType) {
            case 0: // Shading fault
                irradiance *= 0.3;
                current *= 0.3;
                powerOutput *= 0.3;
                break;
            case 1: // Temperature fault
                temperature += 30;
                voltage *= 0.8;
                powerOutput *= 0.7;
                break;
            case 2: // Connection fault
                voltage *= 0.5;
                current *= 0.5;
                powerOutput *= 0.25;
                break;
        }
    }

    // Update state with final values
    global.pvHybridState.irradiance = irradiance;
    global.pvHybridState.temperature = temperature;
    global.pvHybridState.voltage = voltage;
    global.pvHybridState.current = current;
    global.pvHybridState.powerOutput = powerOutput;

    return {
        device_id: 'pv_panels',
        device_type: 'photovoltaic',
        timestamp: now.toISOString(),
        data: {
            irradiance: Math.round(irradiance * 100) / 100,
            temperature: Math.round(temperature * 100) / 100,
            voltage: Math.round(voltage * 100) / 100,
            current: Math.round(current * 100) / 100,
            power_output: Math.round(powerOutput * 100) / 100
        },
        status: isFault ? 'fault' : 'operational',
        location: 'site_a',
        fault_type: isFault ? ['shading', 'temperature', 'connection'][faultType] : null
    };
}

// Generate and return data
const pvPanelsData = generatePVPanelsData();
msg.payload = pvPanelsData;
msg.topic = `devices/photovoltaic/${pvPanelsData.device_id}/telemetry`;

return msg;

// Validate photovoltaic panels data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    irradiance: { min: 0, max: 1200 },
    temperature: { min: -40, max: 100 },
    voltage: { min: 0, max: 100 },
    current: { min: 0, max: 20 },
    power_output: { min: 0, max: 1000 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.power_output > data.data.voltage * data.data.current * 1.1) {
    errors.push('Power output exceeds voltage * current');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const irradiance = parseFloat(data.data.irradiance) || 0;
const temperature = parseFloat(data.data.temperature) || 0;
const voltage = parseFloat(data.data.voltage) || 0;
const current = parseFloat(data.data.current) || 0;
const power_output = parseFloat(data.data.power_output) || 0;

// Calculate efficiency if not provided
const efficiency = data.data.efficiency ? parseFloat(data.data.efficiency) : 
    (power_output > 0 && irradiance > 0) ? (power_output / (irradiance * 0.1)) * 100 : 0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    irradiance: irradiance,
    temperature: temperature,
    voltage: voltage,
    current: current,
    power_output: power_output,
    efficiency: efficiency
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ PV Panels data transformed for InfluxDB:');
node.log('  - Irradiance: ' + irradiance + ' W/m²');
node.log('  - Temperature: ' + temperature + ' °C');
node.log('  - Voltage: ' + voltage + ' V');
node.log('  - Current: ' + current + ' A');
node.log('  - Power Output: ' + power_output + ' W');
node.log('  - Efficiency: ' + efficiency + ' %');
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
