// Wind Turbine Data Simulation
const now = new Date();
const hour = now.getHours();
const dayOfYear = Math.floor((now - new Date(now.getFullYear(), 0, 0)) / (1000 * 60 * 60 * 24));

// Initialize global state for parameter tracking
if (!global.windVAWTState) {
    global.windVAWTState = {
        windSpeed: 8,
        powerOutput: 2000,
        rotorSpeed: 20,
        bladePitch: 0,
        generatorTemp: 30
    };
}

// Helper function to limit changes to 3%
function limitChange(newValue, previousValue) {
    if (previousValue === 0 || previousValue === undefined) return newValue;
    const maxChange = previousValue * 0.03;
    const change = newValue - previousValue;
    if (Math.abs(change) > maxChange) {
        return previousValue + (change > 0 ? maxChange : -maxChange);
    }
    return newValue;
}

// Wind speed model (m/s)
function calculateWindSpeed() {
    const baseWindSpeed = 8;
    const seasonalFactor = 1 + 0.3 * Math.sin(2 * Math.PI * (dayOfYear - 80) / 365);
    const dailyFactor = 1 + 0.2 * Math.sin(2 * Math.PI * (hour - 6) / 24);
    const turbulence = (Math.random() - 0.5) * baseWindSpeed * 0.03; // Limited to 3%
    const newValue = Math.max(0, baseWindSpeed * seasonalFactor * dailyFactor + turbulence);
    return limitChange(newValue, global.windVAWTState.windSpeed);
}

// Power curve model (W)
function calculatePowerOutput(windSpeed) {
    const cutInSpeed = 3;
    const ratedSpeed = 12;
    const cutOutSpeed = 25;
    const ratedPower = 2000;

    if (windSpeed < cutInSpeed || windSpeed > cutOutSpeed) {
        return 0;
    }

    if (windSpeed >= ratedSpeed) {
        return ratedPower;
    }

    // Power curve follows cubic relationship
    const powerFactor = Math.pow((windSpeed - cutInSpeed) / (ratedSpeed - cutInSpeed), 3);
    const newValue = ratedPower * powerFactor;
    return limitChange(newValue, global.windVAWTState.powerOutput);
}

// Rotor speed model (RPM)
function calculateRotorSpeed(windSpeed) {
    const minSpeed = 10;
    const maxSpeed = 30;
    const optimalWindSpeed = 12;

    if (windSpeed < 3) return 0;

    const speedFactor = Math.min(1, windSpeed / optimalWindSpeed);
    const newValue = minSpeed + (maxSpeed - minSpeed) * speedFactor;
    return limitChange(newValue, global.windVAWTState.rotorSpeed);
}

// Blade pitch angle model (degrees)
function calculateBladePitch(windSpeed) {
    const minPitch = 0;
    const maxPitch = 90;
    const optimalWindSpeed = 12;

    if (windSpeed <= optimalWindSpeed) {
        return minPitch;
    }

    const pitchFactor = Math.min(1, (windSpeed - optimalWindSpeed) / 10);
    const newValue = minPitch + (maxPitch - minPitch) * pitchFactor;
    return limitChange(newValue, global.windVAWTState.bladePitch);
}

// Generator temperature model (°C)
function calculateGeneratorTemp(powerOutput) {
    const ambientTemp = 20 + 10 * Math.sin(2 * Math.PI * (hour - 6) / 24);
    const loadFactor = powerOutput / 2000;
    const tempRise = loadFactor * 40;
    const baseTemp = 30;
    const noise = (Math.random() - 0.5) * baseTemp * 0.03; // Limited to 3%
    const newValue = ambientTemp + tempRise + noise;
    return limitChange(newValue, global.windVAWTState.generatorTemp);
}

// Generate data with fault scenarios
function generateWindTurbineData() {
    const faultProbability = 0.001;
    const isFault = Math.random() < faultProbability;

    let windSpeed = calculateWindSpeed();
    let powerOutput = calculatePowerOutput(windSpeed);
    let rotorSpeed = calculateRotorSpeed(windSpeed);
    let bladePitch = calculateBladePitch(windSpeed);
    let generatorTemp = calculateGeneratorTemp(powerOutput);

    // Fault scenarios (faults can exceed 3% limit)
    if (isFault) {
        const faultType = Math.floor(Math.random() * 4);
        switch (faultType) {
            case 0: // Gearbox fault
                rotorSpeed *= 0.7;
                powerOutput *= 0.6;
                generatorTemp += 20;
                break;
            case 1: // Blade damage
                bladePitch += 15;
                powerOutput *= 0.5;
                break;
            case 2: // Generator fault
                powerOutput *= 0.3;
                generatorTemp += 30;
                break;
            case 3: // Sensor fault
                windSpeed *= 1.5;
                powerOutput *= 0.8;
                break;
        }
    }

    // Update state with final values
    global.windVAWTState.windSpeed = windSpeed;
    global.windVAWTState.powerOutput = powerOutput;
    global.windVAWTState.rotorSpeed = rotorSpeed;
    global.windVAWTState.bladePitch = bladePitch;
    global.windVAWTState.generatorTemp = generatorTemp;

    return {
        device_id: 'turbine_vertical',
        device_type: 'wind_turbine',
        timestamp: now.toISOString(),
        data: {
            wind_speed: Math.round(windSpeed * 100) / 100,
            power_output: Math.round(powerOutput * 100) / 100,
            rotor_speed: Math.round(rotorSpeed * 100) / 100,
            blade_pitch: Math.round(bladePitch * 100) / 100,
            generator_temperature: Math.round(generatorTemp * 100) / 100
        },
        status: isFault ? 'fault' : 'operational',
        location: 'site_b',
        fault_type: isFault ? ['gearbox', 'blade_damage', 'generator', 'sensor'][faultType] : null
    };
}

// Generate and return data
const wtData = generateWindTurbineData();
msg.payload = wtData;
msg.topic = `devices/wind_turbine/${wtData.device_id}/telemetry`;

return msg;

// Validate wind turbine data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    wind_speed: { min: 0, max: 50 },
    power_output: { min: 0, max: 2500 },
    rotor_speed: { min: 0, max: 50 },
    blade_pitch: { min: 0, max: 90 },
    generator_temperature: { min: -20, max: 120 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.wind_speed < 3 && data.data.power_output > 0) {
    errors.push('Power output when wind speed below cut-in');
}

if (data.data.wind_speed > 25 && data.data.power_output > 0) {
    errors.push('Power output when wind speed above cut-out');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const windSpeed = parseFloat(data.data.wind_speed) || 0;
const powerOutput = parseFloat(data.data.power_output) || 0;
const rotorSpeed = parseFloat(data.data.rotor_speed) || 0;
const bladePitch = parseFloat(data.data.blade_pitch) || 0;
const generatorTemp = parseFloat(data.data.generator_temperature) || 0;

// Calculate efficiency if not provided
const efficiency = data.data.efficiency ? parseFloat(data.data.efficiency) : 
    (powerOutput > 0 && windSpeed > 3) ? (powerOutput / (0.5 * 1.225 * Math.PI * 25 * Math.pow(windSpeed, 3))) * 100 : 0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    wind_speed: windSpeed,
    power_output: powerOutput,
    rotor_speed: rotorSpeed,
    blade_pitch: bladePitch,
    generator_temperature: generatorTemp,
    efficiency: efficiency
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ Wind turbine data transformed for InfluxDB:');
node.log('  - Wind Speed: ' + windSpeed + ' m/s');
node.log('  - Power Output: ' + powerOutput + ' W');
node.log('  - Rotor Speed: ' + rotorSpeed + ' RPM');
node.log('  - Blade Pitch: ' + bladePitch + ' °');
node.log('  - Generator Temp: ' + generatorTemp + ' °C');
node.log('  - Efficiency: ' + efficiency + ' %');
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
