// Wind Turbine VAWT Data Simulation with strict +/-2% corridor
const now = new Date();

const BASE_VALUES = {
    "wind_speed": 6.09,
    "power_output": 174.07,
    "rotor_speed": 21.65,
    "blade_pitch": 0,
    "generator_temperature": 33.31
};

const LIMITS = {
    "wind_speed": {
        "min": 0,
        "max": 50
    },
    "power_output": {
        "min": 0,
        "max": 2500
    },
    "rotor_speed": {
        "min": 0,
        "max": 50
    },
    "blade_pitch": {
        "min": 0,
        "max": 90
    },
    "generator_temperature": {
        "min": -20,
        "max": 120
    }
};

function randomWithinCorridor(key) {
    const base = BASE_VALUES[key];
    if (typeof base !== 'number') {
        return base;
    }
    const corridor = Math.abs(base) * 0.02;
    const limit = LIMITS[key] || {};
    const min = typeof limit.min === 'number' ? limit.min : base - corridor;
    const max = typeof limit.max === 'number' ? limit.max : base + corridor;
    const noise = corridor === 0 ? 0 : (Math.random() - 0.5) * 2 * corridor;
    const value = base + noise;
    return Math.max(min, Math.min(max, value));
}

function roundValue(value) {
    return Math.round(value * 100) / 100;
}

const data = {};
for (const key of Object.keys(BASE_VALUES)) {
    data[key] = roundValue(randomWithinCorridor(key));
}

const payload = {
    device_id: 'turbine_vertical',
    device_type: 'wind_turbine',
    timestamp: now.toISOString(),
    data,
    status: 'operational',
    location: 'site_b',
    fault_type: null
};

msg.payload = payload;
msg.topic = 'devices/wind_turbine/turbine_vertical/telemetry';

return msg;

// Validate wind turbine data received from MQTT
const data = msg.payload;
const errors = [];

// Check required fields
if (!data.device_id || !data.timestamp || !data.data) {
    errors.push('Missing required fields');
}

// Validate data ranges
const ranges = {
    wind_speed: { min: 0, max: 50 },
    power_output: { min: 0, max: 2500 },
    rotor_speed: { min: 0, max: 50 },
    blade_pitch: { min: 0, max: 90 },
    generator_temperature: { min: -20, max: 120 }
};

for (const [key, range] of Object.entries(ranges)) {
    const value = data.data[key];
    if (value < range.min || value > range.max) {
        errors.push(`${key} out of range: ${value} (${range.min}-${range.max})`);
    }
}

// Check for physical consistency
if (data.data.wind_speed < 3 && data.data.power_output > 0) {
    errors.push('Power output when wind speed below cut-in');
}

if (data.data.wind_speed > 25 && data.data.power_output > 0) {
    errors.push('Power output when wind speed above cut-out');
}

if (errors.length > 0) {
    msg.error = errors;
    msg.status = 'invalid';
} else {
    msg.status = 'valid';
}

return msg;

// Transform data for InfluxDB 2.x format
const data = msg.payload;

// Ensure all numeric values are properly converted
const windSpeed = parseFloat(data.data.wind_speed) || 0;
const powerOutput = parseFloat(data.data.power_output) || 0;
const rotorSpeed = parseFloat(data.data.rotor_speed) || 0;
const bladePitch = parseFloat(data.data.blade_pitch) || 0;
const generatorTemp = parseFloat(data.data.generator_temperature) || 0;

// Calculate efficiency if not provided
const efficiency = data.data.efficiency ? parseFloat(data.data.efficiency) : 
    (powerOutput > 0 && windSpeed > 3) ? (powerOutput / (0.5 * 1.225 * Math.PI * 25 * Math.pow(windSpeed, 3))) * 100 : 0;

// The Node-RED InfluxDB output node expects fields directly in the payload
// and tags as separate message properties
msg.payload = {
    wind_speed: windSpeed,
    power_output: powerOutput,
    rotor_speed: rotorSpeed,
    blade_pitch: bladePitch,
    generator_temperature: generatorTemp,
    efficiency: efficiency
};

// Set tags as message properties
msg.device_id = data.device_id;
msg.device_type = data.device_type;
msg.location = data.location || 'unknown';
msg.status = data.status || 'operational';

// Set timestamp
msg.timestamp = new Date(data.timestamp).getTime();

// Add debug information
node.log('✅ Wind turbine data transformed for InfluxDB:');
node.log('  - Wind Speed: ' + windSpeed + ' m/s');
node.log('  - Power Output: ' + powerOutput + ' W');
node.log('  - Rotor Speed: ' + rotorSpeed + ' RPM');
node.log('  - Blade Pitch: ' + bladePitch + ' °');
node.log('  - Generator Temp: ' + generatorTemp + ' °C');
node.log('  - Efficiency: ' + efficiency + ' %');
node.log('  - Fields count: ' + Object.keys(msg.payload).length);
node.log('  - Tags: device_id=' + msg.device_id + ', device_type=' + msg.device_type);

return msg;
